import './Image.scss';

function Image(imageSrc, title, aspectRatio){

    const aspectRatiostyle = {
        aspectRatio: aspectRatio || '16/9'
    };

    return(
        <div style ={aspectRatiostyle} className="image">
        <img src={imageSrc} alt={title} className="image__inner" />
    </div>
    );
}

export default Image;
