import React, { useState } from "react";
import './VideoUploadPage.scss';
import Header from "../../components/Header/Header";
import Image from "../../components/Image/Image";
import LabelText from "../../components/LabelText/LabelText";
import Textarea from "../../components/Textarea/Textarea";
import InputField from "../../components/InputField/InputField";
import ImagePreview from '../../assets/images/Upload-video-preview.jpg';
import PrimaryButton from "../../components/PrimaryButton/PrimaryButton";
import SecondaryButton from "../../components/SecondaryButton/SecondaryButton";
import ButtonIcon from '../../assets/icons/publish.svg';

const initialValues = {
    title: "",
    description: "",
    titleError: "",
    descriptionError: ""
};

function VideoUploadPage() {
    const [values, setValues] = useState(initialValues);

    const handleInputChange = (event) => {
        const { name, value } = event.target;
        let error = "";

        if (value.length === 0) {
            error = `Error: ${name} can't be empty`;
        }

        setValues({
            ...values,
            [name]: value,
            [`${name}Error`]: error
        });
    };

    const handleFormSubmit = (event) => {
        event.preventDefault();
        if (values.title && values.description) {
            alert("Video uploaded successfully");
        } else {
            setValues({
                ...values,
                titleError: values.title ? "" : "Error: title can't be empty",
                descriptionError: values.description ? "" : "Error: description can't be empty"
            });
        }
    };

    return (
        <main>
            <Header dividerOption="divider-bottom" />
            <section className="upload-video">
                <form className="upload-video__section-container" onSubmit={handleFormSubmit}>
                    <div className="upload-video__header"><h1>Upload Video</h1></div>

                    <div className="upload-video__section-wrapper">
                        <div className="upload-video__section-wrapper-left">
                            <LabelText text="Video Thumbnail" />
                            <Image imageSrc={ImagePreview} altText="Video upload preview image" />
                        </div>

                        <div className="upload-video__section-wrapper-right">
                            <InputField
                                label="Title your video"
                                text="Add a video title"
                                name="title"
                                value={values.title}
                                onChange={handleInputChange}
                                error={values.titleError}
                            />

                            <Textarea
                                label="Add a video description"
                                text="Add a description to your video"
                                textareaHeight="upload-video__section-wrapper-right--textarea"
                                name="description"
                                value={values.description}
                                onChange={handleInputChange}
                                error={values.descriptionError}
                            />
                        </div>
                    </div>

                    <div className="upload-video__section-wrapper-buttons">
                        <SecondaryButton
                            text="Cancel"
                            iconOption="upload-video__secondary-button--icon-none"
                            type="button"
                            isLnkt={false}
                            bntId="cancel"
                        />

                        <PrimaryButton
                            text="Publish"
                            icon={ButtonIcon}
                            type="submit"
                            isLnkt={false}
                            bntId="submit"
                        />
                    </div>
                </form>
            </section>
        </main>
    );
}

export default VideoUploadPage;
