import React from 'react';
import './CommentSection.scss';

function CommentSection({ loadComments }) {
  if (!loadComments || loadComments.length === 0) {
    return <p>No comments available</p>;  // Handle no comments scenario
  }

  return (
    <div className='comment-section'>
      {loadComments.map((comment, index) => (
        <div key={index} className='comment'>
          <p><strong>{comment.name}</strong> {new Date(comment.timestamp).toLocaleDateString()}</p>
          <p>{comment.comment}</p>
        </div>
      ))}
    </div>
  );
}

export default CommentSection;
