import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Home.scss';
import Header from '../../components/Header/Header.js';
import VideoPlayer from '../../components/VideoPlayer/VideoPlayer.js';
import VideoDetails from '../../components/VideoDetails/VideoDetails.js';
import CommentSection from '../../components/CommentSection/CommentSection.js';
import NextVideo from '../../components/NextVideo/NextVideo.js';
import { useParams, useNavigate, Routes, Route } from 'react-router-dom';

const apiUrl = "https://unit-3-project-api-0a5620414506.herokuapp.com/";
const videoPath = "videos";
const apiKey = "0fb16f10-b986-40a5-bdc1-70d836686b86";

function HomePage() {
  const navigate = useNavigate();
  const [currentVideo, setCurrentVideo] = useState(null);
  const [videoList, setVideoList] = useState([]);

  const fetchVideoDetails = async (id) => {
    try {
      const response = await axios.get(`${apiUrl}${videoPath}/${id}?api_key=${apiKey}`);
      setCurrentVideo(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  useEffect(() => {
    const fetchVideos = async () => {
      try {
        const response = await axios.get(`${apiUrl}${videoPath}?api_key=${apiKey}`);
        setVideoList(response.data);
        if (response.data.length > 0) {
          setCurrentVideo(response.data[0]);
        }
      } catch (error) {
        console.error(error);
      }
    };

    fetchVideos();
  }, []);

  return (
    <>
      <Header />
      {currentVideo && (
        <>
          <VideoPlayer videoLink={currentVideo.image} duration={currentVideo.duration} />
          <div className='section-wrapper'>
            <div className='section-wrapper__left'>
              <VideoDetails
                title={currentVideo.title}
                creator={currentVideo.channel}
                uploadDate={new Date(currentVideo.timestamp).toLocaleDateString()}
                views={currentVideo.views}
                likes={currentVideo.likes}
                description={currentVideo.description}
              />
              {Array.isArray(currentVideo.comments) && <CommentSection loadComments={currentVideo.comments} />}
            </div>
            <div className='section-wrapper__right'>
              <NextVideo
                videos={videoList.filter(video => video.id !== currentVideo.id)}
                onVideoSelect={(id) => navigate(`/video/${id}`)}
              />
            </div>
          </div>
        </>
      )}
    </>
  );
}

function VideoDetailsPage() {
  const { videoId } = useParams();
  const [currentVideo, setCurrentVideo] = useState(null);
  const [videoList, setVideoList] = useState([]);
  const navigate = useNavigate();

  const fetchVideoDetails = async (id) => {
    try {
      const response = await axios.get(`${apiUrl}${videoPath}/${id}?api_key=${apiKey}`);
      setCurrentVideo(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  useEffect(() => {
    const fetchVideos = async () => {
      try {
        const response = await axios.get(`${apiUrl}${videoPath}?api_key=${apiKey}`);
        setVideoList(response.data);
      } catch (error) {
        console.error(error);
      }
    };

    fetchVideos();
  }, []);

  useEffect(() => {
    if (videoId) {
      fetchVideoDetails(videoId);
    }
  }, [videoId]);

  return (
    <>
      <Header />
      {currentVideo && (
        <>
          <VideoPlayer videoLink={currentVideo.image} duration={currentVideo.duration} />
          <div className='section-wrapper'>
            <div className='section-wrapper__left'>
              <VideoDetails
                title={currentVideo.title}
                creator={currentVideo.channel}
                uploadDate={new Date(currentVideo.timestamp).toLocaleDateString()}
                views={currentVideo.views}
                likes={currentVideo.likes}
                description={currentVideo.description}
              />
              {Array.isArray(currentVideo.comments) && <CommentSection loadComments={currentVideo.comments} />}
            </div>
            <div className='section-wrapper__right'>
              <NextVideo
                videos={videoList.filter(video => video.id !== currentVideo.id)}
                onVideoSelect={(id) => navigate(`/video/${id}`)}
              />
            </div>
          </div>
        </>
      )}
    </>
  );
}

function Home() {
  return (
    <Routes>
      <Route path="/" element={<HomePage />} />
      <Route path="/video/:videoId" element={<VideoDetailsPage />} />
    </Routes>
  );
}

export default Home;
