import React, { useState } from 'react';
import './Textarea.css'; // Import the CSS file for styling

function Textarea() {
  const [comment, setComment] = useState('');
  const [clicked, setClicked] = useState(false);

  const handleChange = (event) => {
    setComment(event.target.value);
  };

  const handleClick = () => {
    setClicked(true);
  };

  return (
    <form className="comment-textarea">
      <label className="comment-textarea__label">Join the conversation</label>
      <textarea
        className={`comment-textarea__textarea ${clicked && comment === '' ? 'empty' : ''}`}
        placeholder="Add new comment"
        type="text"
        name="Comment"
        rows="1"
        value={comment}
        onChange={handleChange}
        onClick={handleClick}
      ></textarea>
    </form>
  );
}

export default Textarea;
