import React from "react";
import './Textarea.scss';

function Textarea({ text, label, textareaHeight, value, onChange, className }) {
    return (
        <div className={`comment-textarea ${className}`}>
            <label className="comment-textarea__label">{text}</label>
            <textarea 
                className={`comment-textarea__textarea ${textareaHeight} ${className}`} 
                placeholder={label} 
                type="text" 
                name="Comment" 
                rows="1"
                value={value}
                onChange={onChange}
            ></textarea>
        </div>
    );
}

export default Textarea;
