import {React, useRef, useState} from "react";
import './InputField.scss';

function InputField({ text, label, value, onChange }) {
    const [input, setInput] = useState("");
    const formRef = useRef();

    const handleFormSubmit = (event) =>{
        event.preventDefault();

        if (input){
            alert("Video upload succesfully");
        }
    };

    return (
        <div className="input">
            <label className="input__label">{text}</label>
            <input 
                className={`input__textarea ${!value ? 'input__textarea--error' : ''}`} 
                placeholder={label} 
                type="text" 
                name="Input"
                value={`input ${value}`}
                onChange={`onChange ${handleFormSubmit}`}
            />
        </div>
    );
}

export default InputField;
