import React from "react";
import './Textarea.scss';

function Textarea({ text, label, textareaHeight }){
    const inputStyle = {
        height: '40px'
      };

    return(
        <form className="comment-textarea">
            <label className="comment-textarea__label">{text}</label>
            <textarea className="comment-textarea__textarea" placeholder={label} type="text" name="Comment" rows="1" style={{height: textareaHeight}}></textarea>
        </form>
    );
}

export default Textarea;
