                    <div className="upload-video__section-wrapper-buttons">
                        <SecondaryButton text="Cancel" iconOption="upload-video__secondary-button--icon-none" />
                        <PrimaryButton text="Publish" icon={ButtonIcon} />
                    </div>
                </div>
            </section>
        </main>
    );
}

export default VideoUploadPage;
