import React from "react";
import './Textarea.scss';
import { useState } from 'react';

function Textarea(){

    const [comment, setComment] = useState('');
    const [clicked, setClicked] = useState(false);

    const handleChange = (event) => {
      setComment(event.target.value);
    };

    const handleClick = () => {
      setClicked(true);
    };

    return(
        <form className="comment-textarea">
            <label className="comment-textarea__label">Join the conversation</label>
            <textarea    onChange={handleChange}
        onClick={handleClick}className="comment-textarea__textarea" placeholder="Add new comment" type="text" name="Comment" rows="1"></textarea>
        </form>
    );
}

export default Textarea;
